import React, { Component } from "react";
import API from "../../utils/API";

class Results extends Component {
  state = {
    savedBooks: [],
  };
  componentDidMount() {
    API.getBooks()
      .then((savedBooks) => this.setState({ savedBooks: savedBooks }))
      .catch((err) => console.log(err));
  }

  handleSave = (book) => {
    if (this.state.savedBooks.map((book) => book._id).includes(book._id)) {
      API.deleteBook(book._id)
        .then((deletedBook) =>
          this.setState({
            savedBooks: this.state.savedBooks.filter(
              (book) => book._id !== deletedBook._id
            ),
          })
        )
        .catch((err) => console.err(err));
    } else {
      API.saveBook(book)
        .then((savedBook) =>
          this.setState({
            savedBooks: this.state.savedBooks.concat([savedBook]),
          })
        )
        .catch((err) => console.err(err));
    }
  };

  render() {
    if (!this.props.books)
    return(null)
    return (
      <div>
        {!this.props.books.length ? (
          <h1 className="text-center">No Results</h1>
        ) : (
          <div>
            {this.props.books.map((result) => (
              <div className="card mb-3" key={result._id}>
                <div className="row">
                  <div className="col-md-2">
                    <img
                      alt={result.title}
                      className="img-fluid"
                      src={result.image}
                    />
                  </div>
                  <div className="col-md-10">
                    <div className="card-body">
                      <h5 className="card-title">
                        {result.title} by {result.authors}
                      </h5>
                      <p className="card-text">{result.description}</p>
                      <div>
                        <a href={result.link} className="btn" target="_blank">
                          View
                        </a>
                        <button
                          onClick={() => this.handleSave(result)}
                          className="btn"
                        >
                          {this.state.savedBooks
                            .map((book) => book._id)
                            .includes(result._id)
                            ? "Unsave"
                            : "Save"}
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    );
  }
}

export default Results;
